
public class exemplo4casting {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a, b;
		double resultado;
		
		a = 5;
		b = 2;
		
		
		//adicionando a expresão double em frente da conta é executado um casting
		resultado = (double) a / b;
		System.out.println(resultado);	
		

	}

}
